    "SubsidioTipo" integer,
    "SubsidioValor" numeric,
    "EmpresaId" integer NOT NULL,
    CONSTRAINT "PK_Sucursales" PRIMARY KEY ("Id"),
    CONSTRAINT "FK_Sucursales_Empresas_EmpresaId" FOREIGN KEY ("EmpresaId") REFERENCES "Empresas" ("Id") ON DELETE CASCADE
);

CREATE TABLE "OpcionesHorarios" (
    "Id" integer GENERATED BY DEFAULT AS IDENTITY,
    "OpcionId" integer NOT NULL,
    "HorarioId" integer NOT NULL,
    CONSTRAINT "PK_OpcionesHorarios" PRIMARY KEY ("Id"),
    CONSTRAINT "FK_OpcionesHorarios_Horarios_HorarioId" FOREIGN KEY ("HorarioId") REFERENCES "Horarios" ("Id") ON DELETE CASCADE,
    CONSTRAINT "FK_OpcionesHorarios_Opciones_OpcionId" FOREIGN KEY ("OpcionId") REFERENCES "Opciones" ("Id") ON DELETE CASCADE
);

CREATE TABLE "Empleados" (
    "Id" integer GENERATED BY DEFAULT AS IDENTITY,
    "Codigo" text,
    "Nombre" text,
    "Borrado" boolean NOT NULL,
    "SucursalId" integer NOT NULL,
    "EsSubsidiado" boolean NOT NULL DEFAULT TRUE,
    "SubsidioTipo" integer,
    "SubsidioValor" numeric,
    "Estado" integer NOT NULL,
    "EsJefe" boolean NOT NULL,
    CONSTRAINT "PK_Empleados" PRIMARY KEY ("Id"),
