    "IndicacionesEntrega" text NOT NULL,
    CONSTRAINT "PK_Localizaciones" PRIMARY KEY ("Id"),
    CONSTRAINT "FK_Localizaciones_Empresas_EmpresaId" FOREIGN KEY ("EmpresaId") REFERENCES "Empresas" ("Id") ON DELETE CASCADE,
    CONSTRAINT "FK_Localizaciones_Sucursales_SucursalId" FOREIGN KEY ("SucursalId") REFERENCES "Sucursales" ("Id") ON DELETE CASCADE
);

CREATE TABLE "Menus" (
    "Id" integer GENERATED BY DEFAULT AS IDENTITY,
