    CONSTRAINT "FK_Menus_Empresas_EmpresaId" FOREIGN KEY ("EmpresaId") REFERENCES "Empresas" ("Id") ON DELETE RESTRICT,
    CONSTRAINT "FK_Menus_Sucursales_SucursalId" FOREIGN KEY ("SucursalId") REFERENCES "Sucursales" ("Id") ON DELETE RESTRICT
);

CREATE TABLE "SucursalesHorarios" (
    "Id" integer GENERATED BY DEFAULT AS IDENTITY,
